import i18n from "i18next";
import { initReactI18next } from "react-i18next";

import translationsInFinnish from '../locales/fi/translation.json';
import translationsInEnglish from '../locales/en/translation.json';

// the translations
const resources = {
  fi: {
    translation: translationsInFinnish
  },
  en: {
    translation: translationsInEnglish
  },
};

i18n
  .use(initReactI18next) // passes i18n down to react-i18next
  .init({
    resources, // resources are important to load translations for the languages.
    lng: "en", // It acts as default language. When the site loads, content is shown in this language.  
    debug: true,
    fallbackLng: "fi", // use if selected language is not available
    interpolation: {
      escapeValue: false
    },
    ns: "translation", // namespaces help to divide huge translations into multiple small files.
    defaultNS: "translation"
  });

export default i18n;
